mapper.select:
/*根据登录老师的id查询所带班的层次*/
    select * from gradation
    where gradationid in(
    select gradationid from grade
    where gradeid in (
    select gradeid from class
    where classId in(
    select distinct classId
    from teacher t,class_teacher ct
    where t.`teacherId`=ct.teacherId and t.`posterId` < 6 and t.`teacherId`=#{teacherId}
      )
    )
    )
